
public class IfElseExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		//defining a variable
		int num = 13;
		
		//check if the number is divisible by 2 or not
		if(num%2==0) {
			System.out.println("Even number");
		}
		else {
			System.out.println("Odd number");
		}

	}

}
